mapper.select:
select
	mem_no, mem_id, mem_pw, mem_name, mem_gender, mem_email, mem_phone, mem_postcode, mem_address1, mem_address2, mem_agree, mem_profileimg, mem_regdate
from noticemember
where mem_id = #{memId} 
and mem_pw = #{memPw}

select
	mem_id
from noticemember
where mem_email = #{memEmail} 
and mem_name = #{memName}

select
	mem_pw
from noticemember
where mem_id = #{memId} 
and mem_email = #{memEmail} 
and mem_name = #{memName}

select
	m.mem_no, mem_id, mem_pw, mem_name, mem_gender, mem_email, mem_phone, mem_postcode, mem_address1, mem_address2, mem_agree, mem_profileimg, mem_regdate, auth
from noticemember m left outer join noticemember_auth ma on(m.mem_no = ma.mem_no)
where mem_id = #{memId}
